import React, { useState } from 'react';
import './page.css'

const Product = ({ id, name, price, image, addToCart }) => (
  <div className="product-card">
    <img src={image} alt={name} className="product-image" />
    <h3 className="product-name">{name}</h3>
    <p className="product-price">Price: ${price}</p>
    <button onClick={() => addToCart({ id, name, price })} className="add-to-cart-button">Add to Cart</button>
  </div>
);

const Cart = ({ cartItems }) => (
  <div>
    <h2>Cart</h2>
    {cartItems.length === 0 ? (
      <p>Your cart is empty</p>
    ) : (
      cartItems.map(item => (
        <div key={item.id}>
          <p>{item.name} - ${item.price}</p>
        </div>
      ))
    )}
  </div>
);

const ProductsPage = ({ addToCart }) => (
  <div className="products-container">
    <h2>Products</h2>
    <Product id={1} name="Product 1" price={10} image="data:image/jpeg;base64,"  addToCart={addToCart} />
    <Product id={2} name="Product 2" price={20} image="data:image/jpeg;base64," addToCart={addToCart} />
    <Product id={3} name="" price={30} image="product3.jpg" addToCart={addToCart} />
  </div>
);

const AddCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [showProductsPage, setShowProductsPage] = useState(false); 
  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
    setShowProductsPage(true);
  };

  return (
    <div>
      <h1> Add to Cart</h1>
      {showProductsPage ? (
        <Cart cartItems={cartItems} />
      ) : (
        <ProductsPage addToCart={addToCart} />
      )}
    </div>
  );
};

export default AddCart;
